import React, { Component } from "react";
import io from "socket.io-client";

export class App extends Component {
  constructor(props) {
    super(props);
    this.socket = io.connect("http://localhost:4000");
    this.state = {
      question: "",
    };
  }

  componentDidMount() {
    this.socket.on("question", (q) => {
      this.setState({question:q});
    });
  }

  render() {
    return <p>{question}</p>;
  }
}

export default App;
