import React, { Component } from "react";
import io from "socket.io-client";

export class App extends Component {
  constructor(props) {
    super(props);
    this.socket = io.connect("http://localhost:4000");
    this.state = {
      question: "",
      answer: "",
      gameID: "",
      appState: "init",
      isGameCreator: false,
    };
  }

  componentDidMount() {
    this.socket.on("question", (q) => {
      this.setState({ question: q, answer: "" });
    });
    this.socket.on("answer", (a) => {
      this.setState({ answer: a });
    });
    // this.socket.on("new game id", handleNewGameID);
  }

  handleNewGameID = (gameID) => {
    this.setState({ gameID: gameID });
  };

  joinGame = () => {
    this.setState({ isGameCreator: false});
    this.socket.emit('join game', this.state.gameID, (callbackData) =>{
      if(callbackData.sucess){
        this.setState({appState: 'join'})
      } else {
        console.log(callbackData.errorMsg)
      }
    })
  };

  createNewGame = () => {
    this.setState({ isGameCreator: true, appState:'join'  });
    this.socket.emit("create new game", {}, (gameID)=>{
      console.log('got gameid from callback: ' + gameID)
      this.setState({gameID: gameID})
    });
  };

  startGame = () => {
    this.socket.emit("start game", {
      gameID: this.state.gameID,
      data: "whatsup",
    });
    this.setState({appState: 'play'})
  };

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  render() {
    if (this.state.appState === "init") {
      return (
        <div>
          <label for="gameID">join a game:</label>
          <input id="gameID" name="gameID" placeholder="game code" value={this.state.gameID} onChange={this.handleChange}/>
          <button onClick={this.joinGame}>join</button>
          <br />
          ---- or ----
          <br />
          <button onClick={this.createNewGame}>create new game</button>
        </div>
      );
    }
    if (this.state.appState === "join") {
      return (
        <div>
          <p>{this.state.gameID}</p>
          <input placeholder="your name" />
          <button>submit</button>
          {this.state.isGameCreator && (
            <button onClick={this.startGame}>start game</button>
          )}
        </div>
      );
    }
    return (
      <div>
        {this.state.question && (
          <div>
            <p>{this.state.question.question}</p>
            {this.state.question.answers.map((a) => (
              <div>
                <input type="radio" id={a} name="answer" />
                <label for={a}>{a}</label>
              </div>
            ))}
          </div>
        )}
        <p>{this.state.answer}</p>
      </div>
    );
  }
}

export default App;
